import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Container, Typography, Box, CircularProgress, Tabs, Tab, Divider } from '@mui/material';
import { PieChart, Pie, Cell, ResponsiveContainer, Legend, Tooltip } from 'recharts';

import ComplianceStatusCard from '../components/ComplianceStatusCard';
import ComplianceControlsList from '../components/ComplianceControlsList';
import { getMockComplianceData } from '../services/api';
import { getControlFamily } from '../services/mappingService';

// Colors for the pie chart
const COLORS = ['#4caf50', '#ff9800', '#f44336', '#9e9e9e'];

const ProviderPage = () => {
  const { provider } = useParams();
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState(null);
  const [tabValue, setTabValue] = useState(0);
  const [viewMode, setViewMode] = useState("ksi"); // "ksi" or "nist"

  useEffect(() => {
    // In a real app, we would call the API here
    // For now, we'll use mock data
    const fetchData = async () => {
      try {
        // Simulating API call
        await new Promise(resolve => setTimeout(resolve, 500));
        const mockData = getMockComplianceData();
        setData(mockData[provider.toLowerCase()]);
      } catch (error) {
        console.error(`Error fetching data for ${provider}:`, error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [provider]);

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  const handleViewModeChange = (event, newMode) => {
    if (newMode !== null) {
      setViewMode(newMode);
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '50vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  if (!data) {
    return (
      <Container maxWidth="lg">
        <Typography variant="h4" color="error" sx={{ mt: 4 }}>
          Error: Could not load data for {provider}
        </Typography>
      </Container>
    );
  }

  // Prepare data for KSI pie chart
  const ksiChartData = [
    { name: 'Compliant', value: data.compliant, color: COLORS[0] },
    { name: 'Non-Compliant', value: data.nonCompliant, color: COLORS[2] },
    { name: 'Not Applicable', value: data.notApplicable, color: COLORS[3] }
  ];

  // Prepare data for NIST controls pie chart
  const nistChartData = [
    { name: 'Implemented', value: data.nistStats.compliant, color: COLORS[0] },
    { name: 'Partial', value: data.nistStats.partial, color: COLORS[1] },
    { name: 'Not Implemented', value: data.nistStats.nonCompliant, color: COLORS[2] }
  ];

  // Group NIST controls by family
  const controlsByFamily = data.nistControls.reduce((acc, control) => {
    const family = getControlFamily(control.id);
    if (!acc[family]) {
      acc[family] = [];
    }
    acc[family].push(control);
    return acc;
  }, {});

  return (
    <Container maxWidth="lg">
      <Typography variant="h4" sx={{ mt: 4, mb: 2 }}>
        {provider.toUpperCase()} FedRAMP Compliance Dashboard
      </Typography>

      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
        <ComplianceStatusCard provider={provider} stats={data} />

        <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', ml: 2 }}>
          <Tabs 
            value={viewMode} 
            onChange={handleViewModeChange} 
            aria-label="view mode tabs"
          >
            <Tab label="Key Security Indicators" value="ksi" />
            <Tab label="NIST 800-53 Controls" value="nist" />
          </Tabs>
          <Typography variant="caption" color="text.secondary" sx={{ mt: 1 }}>
            {viewMode === "ksi" 
              ? "Viewing compliance by Key Security Indicators (KSIs)" 
              : "Viewing compliance by NIST 800-53 Controls"}
          </Typography>
        </Box>
      </Box>

      <Box sx={{ mb: 4 }}>
        <Tabs 
          value={tabValue} 
          onChange={handleTabChange} 
          aria-label="compliance tabs"
          centered
        >
          <Tab label="Overview" />
          <Tab label="Controls" />
        </Tabs>
      </Box>

      {tabValue === 0 && (
        <Box sx={{ height: 400, display: 'flex', flexDirection: { xs: 'column', md: 'row' } }}>
          <Box sx={{ flex: 1, height: 400 }}>
            <Typography variant="h6" gutterBottom>
              {viewMode === "ksi" ? "KSI Status Breakdown" : "NIST Controls Status Breakdown"}
            </Typography>
            <ResponsiveContainer width="100%" height="90%">
              <PieChart>
                <Pie
                  data={viewMode === "ksi" ? ksiChartData : nistChartData}
                  cx="50%"
                  cy="50%"
                  labelLine={false}
                  outerRadius={80}
                  fill="#8884d8"
                  dataKey="value"
                  label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
                >
                  {(viewMode === "ksi" ? ksiChartData : nistChartData).map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={entry.color} />
                  ))}
                </Pie>
                <Tooltip />
                <Legend />
              </PieChart>
            </ResponsiveContainer>
          </Box>

          <Box sx={{ flex: 1, pl: { md: 4 }, mt: { xs: 4, md: 0 } }}>
            <Typography variant="h6" gutterBottom>
              Summary
            </Typography>
            {viewMode === "ksi" ? (
              <>
                <Typography variant="body1" paragraph>
                  This dashboard shows the FedRAMP KSI compliance status for {provider.toUpperCase()}.
                </Typography>
                <Typography variant="body1" paragraph>
                  Total KSIs: {data.compliant + data.nonCompliant + data.notApplicable}
                </Typography>
                <Typography variant="body1" paragraph>
                  Compliance Rate: {Math.round((data.compliant / (data.compliant + data.nonCompliant)) * 100)}%
                </Typography>
              </>
            ) : (
              <>
                <Typography variant="body1" paragraph>
                  This dashboard shows the NIST 800-53 control compliance status for {provider.toUpperCase()}.
                </Typography>
                <Typography variant="body1" paragraph>
                  Total Controls: {data.nistStats.compliant + data.nistStats.partial + data.nistStats.nonCompliant}
                </Typography>
                <Typography variant="body1" paragraph>
                  Full Compliance: {Math.round((data.nistStats.compliant / (data.nistStats.compliant + data.nistStats.partial + data.nistStats.nonCompliant)) * 100)}%
                </Typography>
                <Typography variant="body1" paragraph>
                  Partial Compliance: {Math.round((data.nistStats.partial / (data.nistStats.compliant + data.nistStats.partial + data.nistStats.nonCompliant)) * 100)}%
                </Typography>
              </>
            )}
            <Typography variant="body1">
              Last Scan: {new Date().toLocaleDateString()}
            </Typography>
          </Box>
        </Box>
      )}

      {tabValue === 1 && (
        <Box sx={{ mt: 2 }}>
          {viewMode === "ksi" ? (
            <>
              <Typography variant="h6" gutterBottom>
                Key Security Indicators Implementation Details
              </Typography>
              <ComplianceControlsList controls={data.controls} type="ksi" />
            </>
          ) : (
            <>
              <Typography variant="h6" gutterBottom>
                NIST 800-53 Controls Implementation Details
              </Typography>

              {Object.keys(controlsByFamily).sort().map(family => (
                <Box key={family} sx={{ mb: 4 }}>
                  <Typography variant="subtitle1" fontWeight="bold" sx={{ mb: 1 }}>
                    {family}
                  </Typography>
                  <Divider sx={{ mb: 2 }} />
                  <ComplianceControlsList 
                    controls={controlsByFamily[family]} 
                    type="nist" 
                  />
                </Box>
              ))}
            </>
          )}
        </Box>
      )}
    </Container>
  );
};

export default ProviderPage;
