import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  Container, Typography, Grid, Card, CardContent, 
  CardActions, Button, Box, LinearProgress 
} from '@mui/material';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { getMockComplianceData } from '../services/api';

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Simulating API call
        await new Promise(resolve => setTimeout(resolve, 500));
        const mockData = getMockComplianceData();
        setData(mockData);
      } catch (error) {
        console.error('Error fetching compliance data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return (
      <Box sx={{ width: '100%', mt: 10 }}>
        <Typography variant="h5" align="center" gutterBottom>
          Loading FedRAMP Compliance Data...
        </Typography>
        <LinearProgress />
      </Box>
    );
  }

  if (!data) {
    return (
      <Container maxWidth="lg">
        <Typography variant="h4" color="error" sx={{ mt: 4 }}>
          Error: Could not load compliance data
        </Typography>
      </Container>
    );
  }

  // Prepare data for comparison chart
  const chartData = [
    {
      name: 'AWS',
      compliant: data.aws.compliant,
      nonCompliant: data.aws.nonCompliant,
      notApplicable: data.aws.notApplicable
    },
    {
      name: 'Azure',
      compliant: data.azure.compliant,
      nonCompliant: data.azure.nonCompliant,
      notApplicable: data.azure.notApplicable
    },
    {
      name: 'GCP',
      compliant: data.gcp.compliant,
      nonCompliant: data.gcp.nonCompliant,
      notApplicable: data.gcp.notApplicable
    }
  ];

  const calculateComplianceRate = (provider) => {
    const { compliant, nonCompliant } = data[provider];
    return Math.round((compliant / (compliant + nonCompliant)) * 100);
  };

  const getComplianceColor = (rate) => {
    if (rate >= 90) return 'success.main';
    if (rate >= 70) return 'warning.main';
    return 'error.main';
  };

  return (
    <Container maxWidth="lg">
      <Typography variant="h4" component="h1" gutterBottom sx={{ mt: 4 }}>
        FedRAMP Controls Validation Dashboard
      </Typography>

      <Typography variant="subtitle1" gutterBottom color="text.secondary">
        Visualizing compliance status across cloud service providers
      </Typography>

      <Box sx={{ mt: 4, mb: 6 }}>
        <Typography variant="h5" gutterBottom>
          CSP Compliance Comparison
        </Typography>
        <ResponsiveContainer width="100%" height={400}>
          <BarChart
            data={chartData}
            margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
          >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="compliant" name="Compliant" fill="#4caf50" />
            <Bar dataKey="nonCompliant" name="Non-Compliant" fill="#f44336" />
            <Bar dataKey="notApplicable" name="Not Applicable" fill="#9e9e9e" />
          </BarChart>
        </ResponsiveContainer>
      </Box>

      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
        <Typography variant="h5" gutterBottom>
          Cloud Service Providers
        </Typography>

        <Button 
          variant="contained" 
          color="primary"
          component={Link}
          to="/validation"
        >
          Validate KSIs
        </Button>
      </Box>

      <Grid container spacing={4}>
        {Object.keys(data).map(provider => {
          const complianceRate = calculateComplianceRate(provider);
          return (
            <Grid item xs={12} md={4} key={provider}>
              <Card>
                <CardContent>
                  <Typography variant="h5" component="div" gutterBottom>
                    {provider.toUpperCase()}
                  </Typography>

                  <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                    <Typography variant="body1" color="text.secondary" sx={{ mr: 1 }}>
                      Compliance Rate:
                    </Typography>
                    <Typography variant="body1" color={getComplianceColor(complianceRate)} fontWeight="bold">
                      {complianceRate}%
                    </Typography>
                  </Box>

                  <LinearProgress 
                    variant="determinate" 
                    value={complianceRate} 
                    color={complianceRate >= 90 ? "success" : complianceRate >= 70 ? "warning" : "error"}
                    sx={{ mb: 2, height: 10, borderRadius: 5 }}
                  />

                  <Typography variant="body2" color="text.secondary">
                    {data[provider].compliant} of {data[provider].compliant + data[provider].nonCompliant} controls implemented
                  </Typography>
                </CardContent>
                <CardActions>
                  <Button 
                    size="small" 
                    component={Link} 
                    to={`/provider/${provider}`}
                  >
                    View Details
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          );
        })}
      </Grid>
    </Container>
  );
};

export default Dashboard;
