import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Typography, 
  Box, 
  Paper, 
  Grid, 
  Tabs, 
  Tab, 
  TextField, 
  InputAdornment,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  Divider
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import FilterListIcon from '@mui/icons-material/FilterList';
import ksiService from '../services/ksiService';

// TabPanel component for tab content
function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`tabpanel-${index}`}
      aria-labelledby={`tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          {children}
        </Box>
      )}
    </div>
  );
}

const ControlCatalog = () => {
  const [tabValue, setTabValue] = useState(0);
  const [controls, setControls] = useState([]);
  const [ksis, setKSIs] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedControl, setSelectedControl] = useState(null);
  const [selectedKSI, setSelectedKSI] = useState(null);

  // Fetch data
  useEffect(() => {
    const fetchData = async () => {
      try {
        const controlsData = await ksiService.getNISTControls();
        const ksisData = await ksiService.getKSIs();
        setControls(controlsData);
        setKSIs(ksisData);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  // Filtered controls based on search
  const filteredControls = controls.filter(control => 
    control.id.toLowerCase().includes(searchQuery.toLowerCase()) || 
    control.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    control.family.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Filtered KSIs based on search
  const filteredKSIs = ksis.filter(ksi => 
    ksi.id.toLowerCase().includes(searchQuery.toLowerCase()) || 
    ksi.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    ksi.description.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Handle tab change
  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
    setSelectedControl(null);
    setSelectedKSI(null);
  };

  // Handle control row click
  const handleControlClick = async (control) => {
    setSelectedControl(control);

    try {
      const relatedKSIs = await ksiService.getKSIsByNISTControl(control.id);
      setSelectedKSI(null);
    } catch (error) {
      console.error("Error fetching related KSIs:", error);
    }
  };

  // Handle KSI row click
  const handleKSIClick = async (ksi) => {
    setSelectedKSI(ksi);

    try {
      const relatedControls = await ksiService.getNISTControlsByKSI(ksi.id);
      setSelectedControl(null);
    } catch (error) {
      console.error("Error fetching related controls:", error);
    }
  };

  // Get status color
  const getStatusColor = (status) => {
    switch (status) {
      case 'Compliant':
        return 'success';
      case 'Non-Compliant':
        return 'error';
      case 'Partial':
        return 'warning';
      default:
        return 'default';
    }
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Typography variant="h4" gutterBottom>
        Control Catalog
      </Typography>
      <Typography variant="body1" color="text.secondary" paragraph>
        Browse and search through NIST 800-53 controls and Key Security Indicators (KSIs)
      </Typography>

      <Paper sx={{ mb: 3, p: 2 }}>
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          <TextField
            fullWidth
            placeholder="Search by ID, name, or family..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <SearchIcon />
                </InputAdornment>
              ),
            }}
          />
        </Box>
      </Paper>

      <Box sx={{ width: '100%' }}>
        <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
          <Tabs value={tabValue} onChange={handleTabChange} aria-label="catalog tabs">
            <Tab label="NIST 800-53 Controls" />
            <Tab label="Key Security Indicators (KSIs)" />
          </Tabs>
        </Box>

        {/* Controls Tab */}
        <TabPanel value={tabValue} index={0}>
          <Grid container spacing={3}>
            <Grid item xs={12} md={selectedControl ? 7 : 12}>
              <TableContainer component={Paper}>
                <Table aria-label="NIST controls table">
                  <TableHead>
                    <TableRow>
                      <TableCell>Control ID</TableCell>
                      <TableCell>Control Name</TableCell>
                      <TableCell>Family</TableCell>
                      <TableCell>Related KSIs</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {filteredControls.map((control) => (
                      <TableRow 
                        key={control.id}
                        hover
                        onClick={() => handleControlClick(control)}
                        sx={{ cursor: 'pointer', backgroundColor: selectedControl?.id === control.id ? 'rgba(0, 0, 0, 0.04)' : 'inherit' }}
                      >
                        <TableCell><strong>{control.id}</strong></TableCell>
                        <TableCell>{control.name}</TableCell>
                        <TableCell>{control.family}</TableCell>
                        <TableCell>{control.relatedKSIs?.length || 0}</TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </Grid>

            {selectedControl && (
              <Grid item xs={12} md={5}>
                <Paper sx={{ p: 3, height: '100%' }}>
                  <Typography variant="h6" gutterBottom>
                    {selectedControl.id}: {selectedControl.name}
                  </Typography>
                  <Typography variant="subtitle1" color="text.secondary">
                    {selectedControl.family}
                  </Typography>
                  <Divider sx={{ my: 2 }} />

                  <Typography variant="subtitle2" gutterBottom>
                    Related Key Security Indicators
                  </Typography>
                  {selectedControl.relatedKSIs && selectedControl.relatedKSIs.length > 0 ? (
                    <Box sx={{ mt: 1 }}>
                      {selectedControl.relatedKSIs.map(ksiId => {
                        const ksi = ksis.find(k => k.id === ksiId);
                        return ksi ? (
                          <Chip 
                            key={ksi.id}
                            label={`${ksi.id}: ${ksi.name}`}
                            color={getStatusColor(ksi.status)}
                            variant="outlined"
                            sx={{ m: 0.5 }}
                          />
                        ) : null;
                      })}
                    </Box>
                  ) : (
                    <Typography variant="body2" color="text.secondary">
                      No related KSIs found
                    </Typography>
                  )}
                </Paper>
              </Grid>
            )}
          </Grid>
        </TabPanel>

        {/* KSIs Tab */}
        <TabPanel value={tabValue} index={1}>
          <Grid container spacing={3}>
            <Grid item xs={12} md={selectedKSI ? 7 : 12}>
              <TableContainer component={Paper}>
                <Table aria-label="KSIs table">
                  <TableHead>
                    <TableRow>
                      <TableCell>KSI ID</TableCell>
                      <TableCell>Name</TableCell>
                      <TableCell>Status</TableCell>
                      <TableCell>Related Controls</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {filteredKSIs.map((ksi) => (
                      <TableRow 
                        key={ksi.id}
                        hover
                        onClick={() => handleKSIClick(ksi)}
                        sx={{ cursor: 'pointer', backgroundColor: selectedKSI?.id === ksi.id ? 'rgba(0, 0, 0, 0.04)' : 'inherit' }}
                      >
                        <TableCell><strong>{ksi.id}</strong></TableCell>
                        <TableCell>{ksi.name}</TableCell>
                        <TableCell>
                          <Chip 
                            label={ksi.status} 
                            color={getStatusColor(ksi.status)} 
                            size="small" 
                          />
                        </TableCell>
                        <TableCell>{ksi.relatedControls?.length || 0}</TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </Grid>

            {selectedKSI && (
              <Grid item xs={12} md={5}>
                <Paper sx={{ p: 3, height: '100%' }}>
                  <Typography variant="h6" gutterBottom>
                    {selectedKSI.id}: {selectedKSI.name}
                  </Typography>
                  <Typography variant="body1" paragraph>
                    {selectedKSI.description}
                  </Typography>
                  <Box sx={{ mt: 1, mb: 2 }}>
                    <Chip 
                      label={selectedKSI.status} 
                      color={getStatusColor(selectedKSI.status)} 
                    />
                  </Box>
                  <Divider sx={{ my: 2 }} />

                  <Typography variant="subtitle2" gutterBottom>
                    Related NIST 800-53 Controls
                  </Typography>
                  {selectedKSI.relatedControls && selectedKSI.relatedControls.length > 0 ? (
                    <Box sx={{ mt: 1 }}>
                      {selectedKSI.relatedControls.map(controlId => {
                        const control = controls.find(c => c.id === controlId);
                        return control ? (
                          <Chip 
                            key={control.id}
                            label={`${control.id}: ${control.name}`}
                            variant="outlined"
                            sx={{ m: 0.5 }}
                          />
                        ) : null;
                      })}
                    </Box>
                  ) : (
                    <Typography variant="body2" color="text.secondary">
                      No related controls found
                    </Typography>
                  )}
                </Paper>
              </Grid>
            )}
          </Grid>
        </TabPanel>
      </Box>
    </Container>
  );
};

export default ControlCatalog;
