import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Container, Typography, Box, CircularProgress, Tabs, Tab } from '@mui/material';
import { PieChart, Pie, Cell, ResponsiveContainer, Legend, Tooltip } from 'recharts';

import ComplianceStatusCard from '../components/ComplianceStatusCard';
import ComplianceControlsList from '../components/ComplianceControlsList';
import { getMockComplianceData } from '../services/api';

// Colors for the pie chart
const COLORS = ['#4caf50', '#f44336', '#9e9e9e'];

const ProviderPage = () => {
  const { provider } = useParams();
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState(null);
  const [tabValue, setTabValue] = useState(0);

  useEffect(() => {
    // In a real app, we would call the API here
    // For now, we'll use mock data
    const fetchData = async () => {
      try {
        // Simulating API call
        await new Promise(resolve => setTimeout(resolve, 500));
        const mockData = getMockComplianceData();
        setData(mockData[provider.toLowerCase()]);
      } catch (error) {
        console.error(`Error fetching data for ${provider}:`, error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [provider]);

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '50vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  if (!data) {
    return (
      <Container maxWidth="lg">
        <Typography variant="h4" color="error" sx={{ mt: 4 }}>
          Error: Could not load data for {provider}
        </Typography>
      </Container>
    );
  }

  // Prepare data for pie chart
  const chartData = [
    { name: 'Compliant', value: data.compliant, color: COLORS[0] },
    { name: 'Non-Compliant', value: data.nonCompliant, color: COLORS[1] },
    { name: 'Not Applicable', value: data.notApplicable, color: COLORS[2] }
  ];

  return (
    <Container maxWidth="lg">
      <Typography variant="h4" sx={{ mt: 4, mb: 2 }}>
        {provider.toUpperCase()} FedRAMP Compliance Dashboard
      </Typography>

      <ComplianceStatusCard provider={provider} stats={data} />

      <Box sx={{ mb: 4 }}>
        <Tabs 
          value={tabValue} 
          onChange={handleTabChange} 
          aria-label="compliance tabs"
          centered
        >
          <Tab label="Overview" />
          <Tab label="Controls" />
        </Tabs>
      </Box>

      {tabValue === 0 && (
        <Box sx={{ height: 400, display: 'flex', flexDirection: { xs: 'column', md: 'row' } }}>
          <Box sx={{ flex: 1, height: 400 }}>
            <Typography variant="h6" gutterBottom>
              Compliance Status Breakdown
            </Typography>
            <ResponsiveContainer width="100%" height="90%">
              <PieChart>
                <Pie
                  data={chartData}
                  cx="50%"
                  cy="50%"
                  labelLine={false}
                  outerRadius={80}
                  fill="#8884d8"
                  dataKey="value"
                  label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
                >
                  {chartData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip />
                <Legend />
              </PieChart>
            </ResponsiveContainer>
          </Box>

          <Box sx={{ flex: 1, pl: { md: 4 }, mt: { xs: 4, md: 0 } }}>
            <Typography variant="h6" gutterBottom>
              Summary
            </Typography>
            <Typography variant="body1" paragraph>
              This dashboard shows the FedRAMP compliance status for {provider.toUpperCase()}.
            </Typography>
            <Typography variant="body1" paragraph>
              Total Controls: {data.compliant + data.nonCompliant + data.notApplicable}
            </Typography>
            <Typography variant="body1" paragraph>
              Compliance Rate: {Math.round((data.compliant / (data.compliant + data.nonCompliant)) * 100)}%
            </Typography>
            <Typography variant="body1">
              Last Scan: {new Date().toLocaleDateString()}
            </Typography>
          </Box>
        </Box>
      )}

      {tabValue === 1 && (
        <Box sx={{ mt: 2 }}>
          <Typography variant="h6" gutterBottom>
            Control Implementation Details
          </Typography>
          <ComplianceControlsList controls={data.controls} />
        </Box>
      )}
    </Container>
  );
};

export default ProviderPage;
