import React from 'react';
import { Card, CardContent, Typography, Box, LinearProgress } from '@mui/material';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import ErrorIcon from '@mui/icons-material/Error';
import HelpIcon from '@mui/icons-material/Help';

const ComplianceStatusCard = ({ provider, stats }) => {
  const totalControls = stats.compliant + stats.nonCompliant + stats.notApplicable;
  const compliancePercentage = Math.round((stats.compliant / (totalControls - stats.notApplicable)) * 100);

  const getStatusColor = () => {
    if (compliancePercentage >= 90) return 'success.main';
    if (compliancePercentage >= 70) return 'warning.main';
    return 'error.main';
  };

  return (
    <Card sx={{ minWidth: 275, mb: 2 }}>
      <CardContent>
        <Typography variant="h5" component="div" gutterBottom>
          {provider.toUpperCase()} Compliance Status
        </Typography>

        <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
          <Typography variant="body1" color="text.secondary" sx={{ mr: 1 }}>
            Compliance Rate:
          </Typography>
          <Typography variant="body1" color={getStatusColor()} fontWeight="bold">
            {compliancePercentage}%
          </Typography>
        </Box>

        <LinearProgress 
          variant="determinate" 
          value={compliancePercentage} 
          color={compliancePercentage >= 90 ? "success" : compliancePercentage >= 70 ? "warning" : "error"}
          sx={{ mb: 2, height: 10, borderRadius: 5 }}
        />

        <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <CheckCircleIcon color="success" sx={{ mr: 0.5 }} />
            <Typography variant="body2">
              Compliant: {stats.compliant}
            </Typography>
          </Box>

          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <ErrorIcon color="error" sx={{ mr: 0.5 }} />
            <Typography variant="body2">
              Non-Compliant: {stats.nonCompliant}
            </Typography>
          </Box>

          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <HelpIcon color="disabled" sx={{ mr: 0.5 }} />
            <Typography variant="body2">
              Not Applicable: {stats.notApplicable}
            </Typography>
          </Box>
        </Box>
      </CardContent>
    </Card>
  );
};

export default ComplianceStatusCard;
