import React from 'react';
import { Card, CardContent, Typography, Box, LinearProgress } from '@mui/material';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import ErrorIcon from '@mui/icons-material/Error';
import WarningIcon from '@mui/icons-material/Warning';
import HelpIcon from '@mui/icons-material/Help';

const ComplianceStatusCard = ({ provider, stats }) => {
  // KSI stats calculation
  const totalKsis = stats.compliant + stats.nonCompliant + stats.notApplicable;
  const ksiCompliancePercentage = Math.round((stats.compliant / (totalKsis - stats.notApplicable)) * 100);

  // NIST stats calculation
  const hasNistStats = stats.nistStats && 
    (stats.nistStats.compliant || stats.nistStats.partial || stats.nistStats.nonCompliant);

  let nistCompliancePercentage = 0;
  let nistPartialPercentage = 0;

  if (hasNistStats) {
    const totalNistControls = stats.nistStats.compliant + stats.nistStats.partial + stats.nistStats.nonCompliant;
    nistCompliancePercentage = Math.round((stats.nistStats.compliant / totalNistControls) * 100);
    nistPartialPercentage = Math.round((stats.nistStats.partial / totalNistControls) * 100);
  }

  const getStatusColor = (percentage) => {
    if (percentage >= 90) return 'success.main';
    if (percentage >= 70) return 'warning.main';
    return 'error.main';
  };

  return (
    <Card sx={{ minWidth: 275, mb: 2, width: '100%' }}>
      <CardContent>
        <Typography variant="h5" component="div" gutterBottom>
          {provider.toUpperCase()} Compliance Status
        </Typography>

        <Box sx={{ mb: 3 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
            <Typography variant="body1" color="text.secondary" sx={{ mr: 1 }}>
              KSI Compliance Rate:
            </Typography>
            <Typography variant="body1" color={getStatusColor(ksiCompliancePercentage)} fontWeight="bold">
              {ksiCompliancePercentage}%
            </Typography>
          </Box>

          <LinearProgress 
            variant="determinate" 
            value={ksiCompliancePercentage} 
            color={ksiCompliancePercentage >= 90 ? "success" : ksiCompliancePercentage >= 70 ? "warning" : "error"}
            sx={{ mb: 2, height: 10, borderRadius: 5 }}
          />

          <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <CheckCircleIcon color="success" sx={{ mr: 0.5 }} />
              <Typography variant="body2">
                Compliant: {stats.compliant}
              </Typography>
            </Box>

            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <ErrorIcon color="error" sx={{ mr: 0.5 }} />
              <Typography variant="body2">
                Non-Compliant: {stats.nonCompliant}
              </Typography>
            </Box>

            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <HelpIcon color="disabled" sx={{ mr: 0.5 }} />
              <Typography variant="body2">
                Not Applicable: {stats.notApplicable}
              </Typography>
            </Box>
          </Box>
        </Box>

        {/* NIST Controls Stats */}
        {hasNistStats && (
          <Box sx={{ mt: 2 }}>
            <Typography variant="subtitle1" gutterBottom>
              NIST 800-53 Controls Status
            </Typography>

            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
              <Typography variant="body2" color="text.secondary" sx={{ mr: 1 }}>
                Full Compliance:
              </Typography>
              <Typography variant="body2" color={getStatusColor(nistCompliancePercentage)} fontWeight="bold">
                {nistCompliancePercentage}%
              </Typography>
            </Box>

            <Box sx={{ display: 'flex', flexDirection: 'column', mb: 1 }}>
              <Box sx={{ height: 8, display: 'flex', mb: 1 }}>
                <Box 
                  sx={{ 
                    width: `${nistCompliancePercentage}%`, 
                    bgcolor: 'success.main',
                    borderTopLeftRadius: 4,
                    borderBottomLeftRadius: 4,
                    borderTopRightRadius: nistPartialPercentage === 0 ? 4 : 0,
                    borderBottomRightRadius: nistPartialPercentage === 0 ? 4 : 0
                  }} 
                />
                <Box 
                  sx={{ 
                    width: `${nistPartialPercentage}%`, 
                    bgcolor: 'warning.main',
                    borderTopRightRadius: nistCompliancePercentage + nistPartialPercentage === 100 ? 4 : 0,
                    borderBottomRightRadius: nistCompliancePercentage + nistPartialPercentage === 100 ? 4 : 0
                  }} 
                />
                <Box 
                  sx={{ 
                    width: `${100 - nistCompliancePercentage - nistPartialPercentage}%`, 
                    bgcolor: 'error.main',
                    borderTopRightRadius: 4,
                    borderBottomRightRadius: 4
                  }} 
                />
              </Box>

              <Box sx={{ display: 'flex', justifyContent: 'space-between', px: 1 }}>
                <Typography variant="caption" color="text.secondary">
                  Full ({stats.nistStats.compliant})
                </Typography>
                <Typography variant="caption" color="text.secondary">
                  Partial ({stats.nistStats.partial})
                </Typography>
                <Typography variant="caption" color="text.secondary">
                  None ({stats.nistStats.nonCompliant})
                </Typography>
              </Box>
            </Box>

            <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 1 }}>
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <CheckCircleIcon color="success" sx={{ mr: 0.5, fontSize: 16 }} />
                <Typography variant="body2">
                  Full: {stats.nistStats.compliant}
                </Typography>
              </Box>

              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <WarningIcon color="warning" sx={{ mr: 0.5, fontSize: 16 }} />
                <Typography variant="body2">
                  Partial: {stats.nistStats.partial}
                </Typography>
              </Box>

              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <ErrorIcon color="error" sx={{ mr: 0.5, fontSize: 16 }} />
                <Typography variant="body2">
                  None: {stats.nistStats.nonCompliant}
                </Typography>
              </Box>
            </Box>
          </Box>
        )}
      </CardContent>
    </Card>
  );
};

export default ComplianceStatusCard;
